    private void btnAgregarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAgregarActionPerformed
        String nomEmpleado = txtNombre.getText();
        String nomDirectivo = txtNombre.getText();
        String nomOficial = txtNombre.getText();
        String nomOperario = txtNombre.getText();
        String nomTecnico = txtNombre.getText();
        
        String empleadoSalida = "";
        String directivoSalida = "";
        String oficialSalida = "";        
        String operarioSalida = "";
        String tecnicoSalida = "";
        
        Empleado empleado = new Empleado(nomEmpleado);
        Directivo directivo = new Directivo(nomDirectivo);        
        Oficial oficial = new Oficial(nomOficial);
        Operario operario = new Operario(nomOperario);
        Tecnico tecnico = new Tecnico(nomTecnico);
        //this.txtSalida.setText(directivoSalida); 
        
       
        for(int i=0; i<1; i++){
           empleadoSalida = empleado.toString();
           txtNombre.setText("");
        }

        for(int i=0; i<1; i++){
           directivoSalida = directivo.toString();
        }
        
        for(int i=0; i<1; i++){
           oficialSalida = oficial.toString();
           txtNombre.setText("");
        }
        
        for(int i=0; i<1; i++){
           operarioSalida = operario.toString();
           txtNombre.setText("");
        }
        
        for(int i=0; i<1; i++){
           tecnicoSalida = tecnico.toString();
           txtNombre.setText("");
        }
        
                
        this.txtSalida.setText(empleadoSalida + "\n" + directivoSalida + "\n" + operarioSalida + 
                "\n" + oficialSalida + "\n" + tecnicoSalida);
        

        // poner e1(do while)(for)

//        String salidaTexto = nomEmpleado + empleadoSalida;
        
//        this.txtSalida.setText(salidaTexto);
    }//GEN-LAST:event_btnAgregarActionPerformed

